const Login = () => {
  return (
    <div>
      <h2>Sign up</h2>
      <form action="/auth" method="post">
        <label for="username">
          <i class="fas fa-user"></i>
        </label>
        <input
          type="text"
          name="username"
          placeholder="Username"
          id="sign-username"
        />

        <label for="username">
          <i class="fas fa-at"></i>
        </label>
        <input type="text" name="email" placeholder="Email" id="sign-email" />

        <label for="password">
          <i class="fas fa-lock"></i>
        </label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="sign-password"
        />
        <input type="submit" value="Sign Up" />
      </form>
    </div>
  );
};

export default Login;
async function loginForm(event) {
  event.preventDefault();

  const email = document.querySelector('#email-login').value.trim()
  const password = document.querySelector('#password-login').value.trim()

  if(email && password) {
      const response = await fetch('/api/login/login', {
          method: 'post',
          body: JSON.stringify({
              email,
              password
          }),
          headers: { 'Content-Type': 'application/json'}
      })
      if(response.ok){
          document.location.replace('/kitchen')
      }else{
          alert(response.statusText)
      }
  }
}


async function signupForm(event) {
  event.preventDefault();

  const username = document.querySelector('#username-signup').value.trim()
  const email = document.querySelector('#email-signup').value.trim()
  const password = document.querySelector('#password-signup').value.trim()
  console.log(username,email,password)

  if(username && email && password) {
      const response = await fetch('/api/login/signup', {
          method: 'post',
          body: JSON.stringify({
              username,
              email,
              password
          }),
          headers: {'Content-type':'application/json'}
      })

      } else {
          alert(response.statusText)
      }
  }


document.querySelector('#loginBtn').addEventListener('click', loginForm)

document.querySelector('#signupBtn').addEventListener('click', signupForm)
